(function(){Markdown.getSharedSecretConverter=function(){if(typeof javaHtmlGeneration=="undefined"){javaHtmlGeneration=false}var a=""+new Date().getTime()+"-"+Math.random();converter=Markdown.getSanitizingConverter(a);var b=function(c,d){return d?" "+c+'="'+d+'"':""};converter.hooks.set("generateImageHTML",function(c){if(javaHtmlGeneration){return javaHtmlGeneration.generateImageHTML(a,c.url,c.alt_text,c.title)}return"<img"+b("src",c.url)+b("alt",c.alt_text)+b("title",c.title)+b("class","markdownImg")+b("data-shared-secret",a)+"/>"});converter.hooks.set("generateLinkHTML",function(c){if(javaHtmlGeneration){return javaHtmlGeneration.generateLinkHTML(a,c.url,c.title,c.link_text)}return"<a"+b("href",c.url)+b("title",c.title)+b("class","markdownLink")+b("data-shared-secret",a)+">"+c.link_text+"</a>"});converter.hooks.set("generateCodeBlockHTML",function(c){return"<pre"+b("class","prettyprint")+b("data-shared-secret",a)+"><code>"+c.codeblock+"</code></pre>"});converter.hooks.set("generateCodeSpanHTML",function(c){return"<code"+b("class","prettyprint")+b("data-shared-secret",a)+">"+c.codespan+"</code>"});return converter}})();Markdown.getSharedSecretConverter();